//WAP to find the cube of a given number.
public class cube {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Please Enter a Number to know its Cube");
		java.util.Scanner sc=new java.util.Scanner(System.in);
		double cu=sc.nextDouble();
		double result=cu*cu*cu;
		System.out.printf("Square of "+cu+ " is %.2f ",result);
//		System.out.println("Square of "+cu+" is "+ result);
				

	}

}
