//WAP to find the square of a given number.
public class square {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Please Enter a Number to know its Square");
		java.util.Scanner sc=new java.util.Scanner(System.in);
		double sq=sc.nextDouble();
		double result=sq*sq;
		System.out.printf("Square of "+sq+ " is %.2f ",result);
//		System.out.println("Square of "+sq+" is "+ result);
				

	}

}
